import React from "react";
import { View, Text, StyleSheet } from "react-native";
import {
  Container,
  Header,
  Content,
  Input,
  Item,
  Button,
  StyleProvider,
} from "native-base";
import { AntDesign } from "@expo/vector-icons";

export default function Login() {
  return (
    <Container>
      <View style={styles.logo}>
        <View style={logo.halftop}>
          <Text style={logo.green}>Green</Text>
        </View>
        {/* <Text style={{ ...logo.halftop, ...logo.green }}>Green</Text> */}
        <View style={logo.halfbottom}>
          <Text style={logo.fingers}>Fingers</Text>
          <Text style={logo.signup}>회원가입</Text>
        </View>
      </View>
      <View style={styles.form}>
        <Item style={styles.singleitem} regular>
          <Input placeholder="이름" />
          <Button style={styles.idcheckbtn}>
            <Text style={styles.textpadding}>중복확인</Text>
            <AntDesign
              size={10}
              style={styles.textpadding}
              name="checkcircle"
            ></AntDesign>
          </Button>
        </Item>
        <Item style={styles.singleitem} regular>
          <Input placeholder="닉네임" />
          <Button style={styles.idcheckbtn}>
            <Text style={styles.textpadding}>중복확인</Text>
            <AntDesign
              size={10}
              style={styles.textpadding}
              name="checkcircle"
            ></AntDesign>
          </Button>
        </Item>
        <Item style={styles.singleitem} regular>
          <Input placeholder="비밀번호" />
        </Item>
        <Item style={styles.singleitem} regular>
          <Input placeholder="비밀번호 확인" />
        </Item>
        <Button style={styles.submitbutton} full>
          <Text>회원가입</Text>
        </Button>
      </View>
    </Container>
  );
}

const logo = StyleSheet.create({
  halftop: {
    flex: 1,
    justifyContent: "flex-start",
    alignSelf: "flex-end",
  },
  halfbottom: {
    flex: 1,
    justifyContent: "flex-start",
    alignItems: "flex-start",
  },
  green: {
    flex: 2,
  },
  fingers: {
    flex: 2,
  },
  signup: {
    flex: 1,
  },
});

const styles = StyleSheet.create({
  logo: {
    flex: 1,
    justifyContent: "center",
    alignItems: "flex-start",
    paddingHorizontal: 40,
  },
  form: {
    flex: 2,
    justifyContent: "flex-start",
    alignItems: "center",
    paddingHorizontal: 40,
  },
  singleitem: {
    justifyContent: "flex-start",
    alignItems: "center",
    marginVertical: 8,
  },
  idcheckbtn: {
    backgroundColor: "transparent",
    paddingRight: 5,
    justifyContent: "center",
    alignSelf: "center",
  },
  textpadding: {
    paddingHorizontal: 3,
  },
  submitbutton: {
    justifyContent: "center",
    alignItems: "center",
    marginVertical: 33,
  },
});
